          Pagecontent::create(array('id' => '165','page_id' => '9','section_id' => '27','contenname' => 'Section_Banner_Title','contenttextenglish' => 'Find Balance and Renewal in Nature','contenttextarabic' => 'Find Balance and Renewal in Nature','created_at' => '2024-10-08 03:00:39','updated_at' => '2024-10-08 03:00:39','deleted_at' => NULL));
          Pagecontent::create(array('id' => '166','page_id' => '9','section_id' => '27','contenname' => 'Section_Banner_Description','contenttextenglish' => 'It\'s important to slow down and reconnect with nature to refresh both mind and body. Enjoy the peace, breathe deeply, and let the natural surroundings bring you back to a state of balance and well-being.','contenttextarabic' => 'It\'s important to slow down and reconnect with nature to refresh both mind and body. Enjoy the peace, breathe deeply, and let the natural surroundings bring you back to a state of balance and well-being.','created_at' => '2024-10-08 03:02:11','updated_at' => '2024-10-08 03:02:11','deleted_at' => NULL));
