          Pagecontent::create(array('id' => '280','page_id' => '3','section_id' => '36','contenname' => 'Contemporary_Luxury_Title','contenttextenglish' => 'Where Classic Camping Meets Contemporary Luxury','contenttextarabic' => 'Where Classic Camping Meets Contemporary Luxury','created_at' => '2024-10-14 07:09:29','updated_at' => '2024-10-14 07:09:29','deleted_at' => NULL));
          Pagecontent::create(array('id' => '281','page_id' => '3','section_id' => '36','contenname' => 'Contemporary_Luxury_Description','contenttextenglish' => 'For a more traditional experience, our premium canvas tents offer the charm of classic camping with modern comforts. Both options include air conditioning, ensuite bathrooms, and stylish furnishings, so you can enjoy the peace of the desert without sacrificing luxury. Whether you pick a dome or a tent, your stay at The Leaf will be an unforgettable glamping experience.','contenttextarabic' => 'For a more traditional experience, our premium canvas tents offer the charm of classic camping with modern comforts. Both options include air conditioning, ensuite bathrooms, and stylish furnishings, so you can enjoy the peace of the desert without sacrificing luxury. Whether you pick a dome or a tent, your stay at The Leaf will be an unforgettable glamping experience.','created_at' => '2024-10-14 07:11:15','updated_at' => '2024-10-14 07:11:15','deleted_at' => NULL));
